package mutableHazardous;

public class string {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1 = "My name";
		String s2 = "Your name";
		String s3 = s1;
		s3 = "His name";
		System.out.println(s1);
		System.out.println(s2);
		System.out.println(s3);
	}

}
